import { useNavigate } from "react-router-dom";
import { useState } from "react";

const Clients= () => {
  const [showForm, setShowForm] = useState(false);

  // ✅ PRODUCTS STATE
  const [clients, setClients] = useState([
    {
      logo: "/restaurant.png",
      title: "Bhagayath Bawarchi",
      category: "Restaurant",
      link: "/ourProducts/restaurant",
    },
    {
      logo: "/client2.jpg",
      title: "TravelGo",
      category: "Travel & Tourism",
      link: "/project/travelgo",
    },
  ]);

  // ✅ FORM STATE
  const [formData, setFormData] = useState({
    logo: "",
    title: "",
    category: "",
    link: "",
  });

  // ✅ HANDLE INPUT CHANGE
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ SAVE PRODUCT
  const handleSave = () => {
    if (!formData.title || !formData.category) return;

    setClients([...clients, formData]);
    setFormData({ logo: "", title: "", category: "", link: "" });
    setShowForm(false);
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-10">
      {/* ===== HEADER ===== */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">
          Our Clients
        </h2>

        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
        >
          + Add Product
        </button>
      </div>


      {/* ===== OUR CLIENTS ===== */}
      <h2 className="text-2xl font-bold text-gray-900 mb-6">
        Our Clients
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        <ProjectCard
          logo="/client1.png"
          title="ABC Restaurants"
          category="Hospitality"
        />

        <ProjectCard
          logo="/client2.jpg"
          title="TravelGo"
          category="Travel & Tourism"
          link="/client/travelgo"
        />
      </div>

    </div>
  );
};

// ================= INLINE PROJECT CARD =================
const ProjectCard = ({ logo, title, category, link }) => {
  const navigate = useNavigate();

  return (
    <div
      className="bg-white rounded-2xl shadow-sm hover:shadow-md transition aspect-square p-5 flex flex-col items-center justify-center text-center"
    >
      <img
        src={logo}
        alt={title}
        className="h-24 w-auto object-contain mb-4"
      />

      <h3 className="text-lg font-semibold text-gray-800">
        {title}
      </h3>

      <p className="text-sm text-gray-500 mb-4">
        {category}
      </p>

      <button
        onClick={() => navigate(link)}
        className="text-blue-600 text-sm font-medium hover:underline"
      >
        View Project →
      </button>
    </div>
  );
};

export default Clients;
