import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Products = () => {
  const [showForm, setShowForm] = useState(false);

  // ✅ PRODUCTS STATE
  const [products, setProducts] = useState([
    {
      logo: "/restaurant.png",
      title: "Bhagayath Bawarchi",
      category: "Restaurant",
      link: "/ourProducts/restaurant",
    },
    {
      logo: "/client2.jpg",
      title: "TravelGo",
      category: "Travel & Tourism",
      link: "/project/travelgo",
    },
  ]);

  // ✅ FORM STATE
  const [formData, setFormData] = useState({
    logo: "",
    title: "",
    duration: "",
    category: "",
    domain: "",
    link: "",
    description:"",
    images:"",
    video:"",
  });

  // ✅ HANDLE INPUT CHANGE
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ SAVE PRODUCT
  const handleSave = () => {
    if (!formData.title || !formData.category) return;

    setProducts([...products, formData]);
    setFormData({ logo: "", title: "", duracategory: "", link: "" });
    setShowForm(false);
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-10">

      {/* ===== HEADER ===== */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">
          Our Products
        </h2>

        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
        >
          + Add Product
        </button>
      </div>

      {/* ===== ADD PRODUCT FORM ===== */}
      {showForm && (
        <div className="bg-white rounded-2xl shadow p-6 mb-10">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Add New Product
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <Input
              label="Product Name"
              name="title"
              value={formData.title}
              onChange={handleChange}
            />
            <Input
              label="Logo "
              name="logo"
              value={formData.logo}
              onChange={handleChange}
            />
            <Input
              label="Duration"
              name="duration"
              value={formData.duration}
              onChange={handleChange}
            />
            <Input
              label="Category"
              name="category"
              value={formData.category}
              onChange={handleChange}
            />
            <Input
              label="Domain"
              name="domain"
              value={formData.domain}
              onChange={handleChange}
            />
            <Input
              label="Website Link"
              name="link"
              value={formData.link}
              onChange={handleChange}
            />
            <Input
              label="Project Description"
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
            <Input
              label="Images"
              name="images"
              value={formData.images}
              onChange={handleChange}
            />
            <Input
              label="Video-Preview"
              name="videos"
              value={formData.video}
              onChange={handleChange}
            />

          </div>

          <div className="flex gap-4 mt-6">
            <button
              onClick={handleSave}
              className="bg-green-600 text-white px-5 py-2 rounded-lg hover:bg-green-700"
            >
              Save Product
            </button>
            <button
              onClick={() => setShowForm(false)}
              className="border px-5 py-2 rounded-lg hover:bg-gray-100"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* ===== PRODUCTS GRID ===== */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {products.map((product, index) => (
          <ProjectCard key={index} {...product} />
        ))}
      </div>
    </div>
  );
};

// ================= INPUT =================
const Input = ({ label, name, value, onChange }) => (
  <div>
    <label className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <input
      type="text"
      name={name}
      value={value}
      onChange={onChange}
      className="w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
  </div>
);

// ================= PROJECT CARD =================
const ProjectCard = ({ logo, title, category, link }) => {
  const navigate = useNavigate();

  return (
    <div className="bg-white rounded-2xl shadow-sm hover:shadow-md transition aspect-square p-5 flex flex-col items-center justify-center text-center">
      <img
        src={logo}
        alt={title}
        className="h-24 w-auto object-contain mb-4"
      />

      <h3 className="text-lg font-semibold text-gray-800">
        {title}
      </h3>

      <p className="text-sm text-gray-500 mb-4">
        {category}
      </p>

      <button
        onClick={() => navigate(link)}
        className="text-blue-600 text-sm font-medium hover:underline"
      >
        View Project →
      </button>
    </div>
  );
};

export default Products;
