import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const Restaurant = () => {
  const { state } = useLocation();

  /* ================= FALLBACK DATA ================= */
  const fallbackImages = [
    "/rest-1.png",
    "/rest-2.png",
    "/rest-3.png",
  ];

  const images = state?.images?.length ? state.images : fallbackImages;
  const description = state?.description || "";
  const video = state?.video || "";
  const title = state?.title || "Bhagayath Bawarchi Restaurant";
  const logo = state?.logo || "/restaurant.png";

  const [current, setCurrent] = useState(0);

  const prevSlide = () => {
    setCurrent(current === 0 ? images.length - 1 : current - 1);
  };

  const nextSlide = () => {
    setCurrent(current === images.length - 1 ? 0 : current + 1);
  };

  return (
    <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow p-8 space-y-10">

      {/* TITLE & LOGO */}
      <div className="flex items-center gap-6">
        <img
          src={logo}
          alt="Client Logo"
          className="w-24 h-24 rounded-xl object-contain"
        />
        <h1 className="text-3xl font-bold text-gray-900">
          {title}
        </h1>
      </div>

      {/* PROJECT DETAILS */}
      <div className="grid grid-cols-1 sm:grid-cols-4 gap-6">
        <Detail label="Duration" value={state?.duration || "—"} />
        <Detail label="Category" value={state?.category || "—"} />
        <Detail label="Domain" value={state?.domain || "—"} />
        <Detail
          label="Website"
          value={
            state?.link ? (
              <a
                href={state.link}
                target="_blank"
                rel="noreferrer"
                className="text-blue-600 hover:underline"
              >
                Visit Website
              </a>
            ) : "—"
          }
        />
      </div>

      <hr className="border-gray-300" />

      {/* DESCRIPTION */}
      {description && (
        <div>
          <h2 className="text-xl font-semibold text-gray-800 mb-3">
            Project Description
          </h2>
          <p className="text-gray-600 leading-relaxed">
            {description}
          </p>
        </div>
      )}

      <hr className="border-gray-300" />

      {/* IMAGE CAROUSEL */}
      <div>
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Project Images
        </h2>

        <div className="relative max-w-4xl mx-auto">
          <img
            src={images[current]}
            alt={`Project Screenshot ${current + 1}`}
            className="rounded-xl shadow-lg w-full transition-all duration-500"
          />

          {/* Left Button */}
          <button
            onClick={prevSlide}
            className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 px-3 py-2 rounded-full shadow"
          >
            ‹
          </button>

          {/* Right Button */}
          <button
            onClick={nextSlide}
            className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white text-gray-800 px-3 py-2 rounded-full shadow"
          >
            ›
          </button>

          {/* Dots */}
          <div className="flex justify-center gap-2 mt-4">
            {images.map((_, i) => (
              <span
                key={i}
                onClick={() => setCurrent(i)}
                className={`w-3 h-3 rounded-full cursor-pointer ${
                  current === i ? "bg-gray-800" : "bg-gray-300"
                }`}
              />
            ))}
          </div>
        </div>
      </div>

      <hr className="border-gray-300" />

      {/* VIDEO SECTION */}
      {video && (
        <div>
          <h2 className="text-xl font-semibold text-gray-800 mb-4">
            Video Preview
          </h2>

          <div className="aspect-video rounded-xl overflow-hidden max-w-3xl mx-auto">
            <iframe
              className="w-full h-full"
              src={video}
              title="Project Video"
              allowFullScreen
            />
          </div>
        </div>
      )}
    </div>
  );
};

/* ================= DETAIL CARD ================= */
const Detail = ({ label, value }) => (
  <div className="bg-gray-200 rounded-xl p-4">
    <p className="text-sm text-gray-700">{label}</p>
    <p className="font-semibold text-gray-800">{value}</p>
  </div>
);

export default Restaurant;
