import { FiUsers, FiBox } from "react-icons/fi";

const Dashboard = () => {
  return (
    <div className="min-h-screen bg-gray-100 p-6 space-y-10">

      {/* DASHBOARD TITLE */}
      <h1 className="text-2xl font-bold text-gray-900">
        Dashboard
      </h1>

      {/* TOP CARDS */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 w-full">
        <StatCard title="No of Clients" value="12" color="blue" icon={<FiUsers />} />
        <StatCard title="No of Products" value="6" color="green" icon={<FiBox />} />
        <StatCard title="Ongoing Projects" value="6" color="green" icon={<FiBox />} />
      </div>

      {/* OUR TEAM SECTION */}
      <div>
        <h2 className="text-xl font-semibold text-gray-900 mb-4">
          Our Team
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 w-full">
          <StatCard title="IT" value="25" color="indigo" />
          <StatCard title="Digital Marketing" value="15" color="purple" />
          <StatCard title="Sales" value="10" color="amber" />
        </div>
      </div>

    </div>
  );
};

/* REUSABLE CARD */
const StatCard = ({ title, value, icon, color }) => {
  const colors = {
    blue: "bg-blue-100 text-blue-600",
    green: "bg-green-100 text-green-600",
    indigo: "bg-indigo-100 text-indigo-600",
    purple: "bg-purple-100 text-purple-600",
    amber: "bg-amber-100 text-amber-600",
  };

  return (
    <div className="bg-white rounded-2xl shadow-sm p-8 flex flex-col items-center gap-4">
      {icon && (
        <div className={`${colors[color]} p-5 rounded-2xl text-2xl`}>
          {icon}
        </div>
      )}
      <p className="text-gray-500 text-sm">{title}</p>
      <h2 className="text-4xl font-bold text-gray-900">{value}</h2>
    </div>
  );
};

export default Dashboard;
