import ProjectCard from "../components/ProjectCard";

const projects = [
  {
    title: "Restaurant Website",
    description: "Modern food ordering & menu management system.",
    status: "Live",
  },
  {
    title: "HR Management System",
    description: "Employee onboarding, attendance & payroll.",
    status: "In Progress",
  },
  {
    title: "E-Commerce Platform",
    description: "Online store with payments and order tracking.",
    status: "Live",
  },
  {
    title: "Travel Agency Portal",
    description: "Tour packages, bookings & enquiries dashboard.",
    status: "In Progress",
  },
];

const Dashboard = () => {
  return (
    <div className="max-w-7xl mx-auto px-6 py-10">
      {/* Page Title */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Company Projects
        </h1>
        <p className="text-gray-500 mt-2">
          Showcase and manage all company projects from one place.
        </p>
      </div>

      {/* Project Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project, index) => (
          <ProjectCard key={index} {...project} />
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
