import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Products = () => {
  const [showForm, setShowForm] = useState(false);

  /* ================= PRODUCTS STATE ================= */
  const [products, setProducts] = useState([
    {
      logo: "/restaurant.png",
      title: "Bhagayath Bawarchi",
      category: "Restaurant",
      link: "/ourProducts/restaurant",
      description: "Restaurant menu management system",
      images: ["/rest-1.png", "/rest-2.png"],
      video: "https://www.youtube.com/embed/dQw4w9WgXcQ",
      status: "live",
    },
  ]);

  /* ================= FORM STATE ================= */
  const [formData, setFormData] = useState({
    logo: "",
    title: "",
    category: "",
    link: "",
    description: "",
    images: [],
    video: "",
    status: "live",
  });

  /* ================= INPUT HANDLERS ================= */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImagesChange = (e) => {
    setFormData({
      ...formData,
      images: Array.from(e.target.files),
    });
  };

  /* ================= SAVE PRODUCT ================= */
  const handleSave = () => {
    if (!formData.title || !formData.category) return;

    const imageUrls = formData.images.map((img) =>
      URL.createObjectURL(img)
    );

    setProducts([
      ...products,
      {
        ...formData,
        images: imageUrls,
      },
    ]);

    setFormData({
      logo: "",
      title: "",
      category: "",
      link: "",
      description: "",
      images: [],
      video: "",
      status: "live",
    });

    setShowForm(false);
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-10">

      {/* HEADER */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">
          Our Products
        </h2>

        <button
          onClick={() => setShowForm(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          + Add Product
        </button>
      </div>

      {/* ADD PRODUCT FORM */}
      {showForm && (
        <div className="bg-white rounded-2xl shadow p-6 mb-10">
          <h3 className="text-lg font-semibold mb-4">
            Add New Product
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

            <Input label="Product Name" name="title" value={formData.title} onChange={handleChange} />
            <Input label="Logo URL" name="logo" value={formData.logo} onChange={handleChange} />
            <Input label="Category" name="category" value={formData.category} onChange={handleChange} />
            <Input label="Website Link" name="link" value={formData.link} onChange={handleChange} />

            {/* DESCRIPTION */}
            <div className="md:col-span-2">
              <label className="block text-sm font-medium mb-1">
                Description
              </label>
              <textarea
                name="description"
                rows="4"
                value={formData.description}
                onChange={handleChange}
                className="w-full border rounded-lg px-3 py-2"
              />
            </div>

            {/* IMAGES */}
            <div className="md:col-span-2">
              <label className="block text-sm font-medium mb-1">
                Images
              </label>
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleImagesChange}
              />

              {/* IMAGE PREVIEW */}
              {formData.images.length > 0 && (
                <div className="flex gap-3 mt-3 flex-wrap">
                  {formData.images.map((img, i) => (
                    <img
                      key={i}
                      src={URL.createObjectURL(img)}
                      alt="preview"
                      className="h-20 w-20 object-cover rounded-lg"
                    />
                  ))}
                </div>
              )}
            </div>

            {/* VIDEO */}
            <Input
              label="Video Preview (YouTube URL)"
              name="video"
              value={formData.video}
              onChange={handleChange}
            />

            {/* STATUS */}
            <div>
              <label className="block text-sm font-medium mb-1">
                Project Status
              </label>
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                className="w-full border rounded-lg px-3 py-2"
              >
                <option value="live">Live</option>
                <option value="ongoing">Ongoing</option>
              </select>
            </div>
          </div>

          <div className="flex gap-4 mt-6">
            <button
              onClick={handleSave}
              className="bg-green-600 text-white px-5 py-2 rounded-lg"
            >
              Save Product
            </button>
            <button
              onClick={() => setShowForm(false)}
              className="border px-5 py-2 rounded-lg"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* PRODUCTS GRID */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {products.map((product, index) => (
          <ProjectCard key={index} product={product} />
        ))}
      </div>
    </div>
  );
};

/* ================= INPUT ================= */
const Input = ({ label, name, value, onChange }) => (
  <div>
    <label className="block text-sm font-medium mb-1">
      {label}
    </label>
    <input
      type="text"
      name={name}
      value={value}
      onChange={onChange}
      className="w-full border rounded-lg px-3 py-2"
    />
  </div>
);

/* ================= PROJECT CARD ================= */
const ProjectCard = ({ product }) => {
  const navigate = useNavigate();

  const statusStyle =
    product.status === "live"
      ? "bg-green-100 text-green-700"
      : "bg-yellow-100 text-yellow-700";

  return (
    <div className="relative bg-white rounded-2xl shadow p-5 text-center">

      {/* STATUS */}
      <span className={`absolute top-3 right-3 text-xs px-3 py-1 rounded-full ${statusStyle}`}>
        {product.status}
      </span>

      <img
        src={product.logo}
        alt={product.title}
        className="h-20 mx-auto mb-4"
      />

      <h3 className="font-semibold">{product.title}</h3>
      <p className="text-sm text-gray-500 mb-3">
        {product.category}
      </p>

      <button
        onClick={() => navigate(product.link, { state: product })}
        className="text-blue-600 text-sm hover:underline"
      >
        View Project â†’
      </button>
    </div>
  );
};

export default Products;
